import {HYDRATE} from 'next-redux-wrapper';



const initialState = {
    user: {
        
    },
    post: {
        
    }
}




// action creater
export const loginAction = (data) => {
    return {
        type: 'LOG_IN',
        data
    }
}



// action creater
export const logoutAction = (data) => {
    return {
        type: 'LOG_OUT'
    }
}



// (이전상태, 액션) => 다음상태
const rootReducer = (state = initialState, action) => {
    switch (action.type) {
        case HYDRATE:
            return {...state, ...action.payload}


        case 'LOG_IN':
            return {
                ...state,
                user: {
                    ...state.user,
                    isLoggedIn: action.data,
                }
            };

        case 'LOG_OUT':
            return {
                ...state,
                user: {
                    ...state.user,
                    isLoggedIn: false,
                    user: null
                }
            };
        default:
            return state;
    }
};


export default rootReducer;
