// imagePaths-이미지경로 
export const initialState = {
    mainPosts: [{
        id:1,
        User: {
            id:1,
            nickname: '제로초',
        },
        content: '첫번째 게시글 #해시태크 #익스프레스',
        Images: [{
            src: 'https://bookthumb-phinf.pstatic.net/cover/137/995/13799585.jpg?udate=20180726',
        }, {
            src: 'https://gimg.gilbut.co.kr/book/BN001958/rn_view_BN001958.jpg',
        }, {
            src: 'https://gimg.gilbut.co.kr/book/BN001998/rn_view_BN001998.jpg',
        }],
        Comments: [{
            User: {
                nickname: 'nero'
            },
            content: '우리 개정판이 나왓군요'
        }, {
            User: {
                nickname: 'hero'
            },
            content: '얼른 사고 싶어요~'
        }]
    }],
    imagePaths: [],
    addPostLoading: false,
    addPostDone: false,
    addPostError: null
}   


export const ADD_POST_REQUEST = 'ADD_POST_REQUEST';
export const ADD_POST_SUCCESS = 'ADD_POST_SUCCESS';
export const ADD_POST_FAILURE = 'ADD_POST_FAILURE';


export const ADD_COMMENT_REQUEST = 'ADD_CONMMENT_REQUEST';
export const ADD_COMMENT_SUCCESS = 'ADD_CONMMENT_SUCCESS';
export const ADD_COMMENT_FAILURE = 'ADD_CONMMENT_FAILURE';




export const addpost = (data) => ({
    type: ADD_POST_REQUEST,
    data
});


export const addComment = (data) => ({
    type: ADD_CONMMENT_REQUEST,
    data
});




const dummyPost = {
    id: 2,
    content: '더미데이터입니다.',
    User: {
        id: 1,
        nickname: '제로초'
    },
    Images: [],
    Comments: []
}




const reducer = (state = initialState, action) => {
    switch (action.type) {
        case ADD_POST_REQUEST:
            return {
                ...state,
                addPostLoading: true,
                addPostDone: false,
                addPostError: null
            };
        
        case ADD_POST_SUCCESS:
            return {
                ...state,
                mainPosts: [dummyPost, ...state.mainPosts],
                addPostLoading: false,
                addPostDone: true,
            };

        case ADD_POST_FAILURE:
            return {
                addPostLoading: false,
                addPostError: action.error
            };


        case ADD_COMMENT_REQUEST:
            return {
                ...state,
                addPostLoading: true,
                addPostDone: false,
                addPostError: null
            };
        
        case ADD_COMMENT_SUCCESS:
            return {
                ...state,
                mainPosts: [dummyPost, ...state.mainPosts],
                addPostLoading: false,
                addPostDone: true,
            };

        case ADD_COMMENT_FAILURE:
            return {
                addPostLoading: false,
                addPostError: action.error
            };

        default:
            return state;
    }
};

export default reducer;
