        <Form onFinish={onSubmitComment}>
            <Form.Item>
                <Input.Textarea value={commentText} onChaage={onChangeCommentText} row={4}/>
                <Button type="primary" htmlType="submit"></Button>

            </Form.Item>
        </Form>
    )
}

CommentForm.propTypes = {
    post: PropTypes.object.isRequired,

