import {all, fork, call, put, takeEvery, takeLatest} from 'redux-saga/effects';
import axios from 'axios';



export default function* rootSaga() {
    yield all([
        fork(watchLogIn), //call이랑은 다르다
        fork(watchLogOut),
        fork(watchAddPost)
    ])
}
