import React from 'react';
import { Card } from 'antd';
import { RetweetOutlined, HeartOutlined, MessageOutlined, EllipsisOutlined } from '@ant-design';


const PostCard = ({post}) => {
    return (
        <div>
            <Card
                cover={post.Images[0] && <PostImages images={post.Images} />}
                actions={[
                    <RetweetOutlined />,
                    <HeartOutlined />,
                    <MessageOutlined />,
                    <Popover>
                        <EllipsisOutlined />
                    </Popover>
                ]}
            >
                <Image />
                <Content />
                <Buttons></Buttons>
            </Card>

            <CommentForm />
            <Comments />
        </div>
    )
}


export default PostCard;
