import React, {useCallback, useState} from 'react';
import Head from 'next/head';
import { Form, Input } from 'antd';
import AppLayout from '../components/AppLayout'
import useinput from '../hooks/useinput';


const Signup = () => {

    const [id, onChangeId] = useinput('');


    const [nickname, onChangeNickname] = useinput('');



    const [passwowrd, setPassword] = useState('');
    const onChangePassword = useCallback((e) => {
        setPassword(e.target.value);
    }, []);




    const onSubmit = useCallback(() => {
            
    }, []);


    return (
        <AppLayout>
            <Head>
                <title>회원가입 | Nodebird</title>
            </Head>
            <Form onFinish={onSubmit}>
                <div>
                    <label htmlFor="user-id">아이디</label>
                    <br />
                    <Input name="user-id" value={id} required onChange={onChangeId} />
                </div>

                <div>
                    <label htmlFor="user-nick">닉네임</label>
                    <br />
                    <Input name="user-nick" value={nickname} required onChange={onChangeNickname} />
                </div>

                <div>
                    <label htmlFor="user-passwowrd">비밀번호</label>
                    <br />
                    <Input name="user-passwowrd" value={passwowrd} required onChange={onChangePassword} />
                </div>

                <div>
                    <label htmlFor="user-passwowrd-check">비밀번호체크</label>
                    <br />
                    <Input
                        name="user-passwowrd-check"
                        value={passwowrdCheck}
                        required
                        onChange={onChangePasswordCheck}
                    />
                    
                </div>


            </Form>
        
        </AppLayout>
    )
}


export default Signup;
