// imagePaths-이미지경로 
export const initialState = {
    mainPosts: [{
        id:1,
        User: {
            id:1,
            nickname: '제로초',
        },
        content: '첫번째 게시글 #해시태크 #익스프레스',
        Image: [{
            src: 'https://images.k-auction.com/www/KMall/Work/0123/12300204001.jpg'
        }, {
            src: 'https://images.k-auction.com/www/KMall/Work/0123/12300304001.jpg'
        }, {
            src: 'https://images.k-auction.com/www/KMall/Work/0123/12300404001.jpg'
        }],
        Comments: [{
            User: {
                nickname: 'nero'
            },
            content: '우리 개정판이 나왓군요'
        }, {
            User: {
                nickname: 'hero'
            },
            content: '얼른 사고 싶어요~'
        }]
    }],
    imagePaths: [],
    postAdded: false,
}   


const ADD_POST = 'ADD_POST'


const reducer = (state = initialState, action) => {
    switch (action.type) {
        default:
            return state;
    }
};

export default reducer;
